// Popup.jsx
import React, { useState } from 'react';
import "./popup.css";

const Popup = ({ item, closePopup }) => {
  const [currentIndex, setCurrentIndex] = useState(item.id - 1); // Utilise l'id pour déterminer l'index
  const [projects] = useState(projectsData); // Assurez-vous d'importer projectsData ici

  const nextProject = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === projects.length - 1 ? 0 : prevIndex + 1
    );
  };

  const prevProject = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? projects.length - 1 : prevIndex - 1
    );
  };

  const currentProject = projects[currentIndex];

  return (
    <div className="popup">
      <div className="popup__content">
        <span className="popup__close" onClick={closePopup}>
          &times;
        </span>
        <img src={currentProject.image} alt={currentProject.title} className="popup__img" />
        <h3 className="popup__title">{currentProject.title}</h3>
        <p className="popup__description">{currentProject.description}</p>
        <div className="popup__navigation">
          <button className="popup__prev" onClick={prevProject}>Previous</button>
          <button className="popup__next" onClick={nextProject}>Next</button>
        </div>
      </div>
    </div>
  );
};

export default Popup;
