// Popup.jsx
import React from 'react';
import "./popup.css";

const Popup = ({ item, closePopup }) => {
  return (
    <div className="popup">
      <div className="popup__content">
        <span className="popup__close" onClick={closePopup}>
          &times;
        </span>
        <img src={item.image} alt={item.title} className="popup__img" />
        <h3 className="popup__title">{item.title}</h3>
        <p className="popup__description">{item.description}</p>
        <div className="popup__navigation">
          <button className="popup__prev">Previous</button>
          <button className="popup__next">Next</button>
        </div>
      </div>
    </div>
  );
};

export default Popup;
