import React, { useState } from "react";

const WorkItems = ({ item }) => {
  const [showModal, setShowModal] = useState(false);

  const handleDemoClick = (e) => {
    e.preventDefault();
    setShowModal(true);
    document.body.style.overflow = 'hidden'; // Optionnel: empêcher le scroll derrière
  };

  const handleCloseModal = () => {
    setShowModal(false);
    document.body.style.overflow = 'auto'; // Restaurer le scroll
  };

  return (
    <div className={`work__card${showModal ? " blurred" : ""}`} key={item.id}>
      <img src={item.image} alt="" className="work__img" />
      <h3 className="work__title">{item.title}</h3>
      <a
        href="#"
        className="work__button"
        onClick={handleDemoClick}
      >
        Demo
        <i className="bx bx-right-arrow-alt work__button-icon"></i>
      </a>

      {/* Modal */}
      {showModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div
            className="modal-content animated"
            onClick={e => e.stopPropagation()}
          >
            <button className="modal-close" onClick={handleCloseModal}>
              &times;
            </button>
            <img src={item.image} alt="Preview" className="modal-img" />
          </div>
        </div>
      )}
    </div>
  );
};

export default WorkItems;
