// WorkItem.jsx
import React, { useState } from "react";
import Popup from "./Popup"; // Assurez-vous d'importer le composant Popup

const WorkItems = ({ item }) => {
  const [showPopup, setShowPopup] = useState(false);

  const handlePopupOpen = () => {
    setShowPopup(true);
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  return (
    <>
      <div className="work__card" key={item.id}>
        <img src={item.image} alt="" className="work__img" />
        <h3 className="work__title">{item.title}</h3>
        <button onClick={handlePopupOpen} className="work__button">
          Demo
          <i className="bx bx-right-arrow-alt work__button-icon"></i>
        </button>
      </div>

      {/* Affiche le popup si showPopup est true */}
      {showPopup && <Popup item={item} closePopup={handlePopupClose} />}
    </>
  );
};

export default WorkItems;
